package chapter02;

public class Q4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 10;
		double d = 2.0;
		
		System.out.println("+: " +((int)(i+d)));
		System.out.println("-: " +((int)(i-d)));
		System.out.println("*: " +((int)(i*d)));
		System.out.println("/: " +((int)(i/d)));

	}

}
