package chapter03;

public class OperatorEx12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 10;
		int numa = 20;
		
		num += numa;
		System.out.println(num);
		
		num -= numa;
		System.out.println(num);
		
		num *= 2;
		System.out.println(num);
		
		num /= 2;
		System.out.println(num);
		
		num %= 3;
		System.out.println(num);                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
		
	}

}
